import React, { useState, useEffect, useRef } from "react";
import { Copy, Send, Plus, Edit3, Check, X, MessageSquare } from "lucide-react";

function App() {
  const [chats, setChats] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [editingChatId, setEditingChatId] = useState(null);
  const [tempTitle, setTempTitle] = useState("");
  const messagesEndRef = useRef(null);

  // Get current chat object
  const currentChat = chats.find((chat) => chat.id === currentChatId);

  // Auto scroll to bottom
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [currentChat?.messages, isTyping]);

  // Copy to clipboard function
  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      console.log("Code copied to clipboard!");
    } catch (err) {
      console.error("Failed to copy: ", err);
    }
  };

  // Enhanced markdown renderer
  const renderBotMessage = (content) => {
    const sections = content.split(/(```[\s\S]*?```|#{1,6}.*)/g);

    return sections.map((section, index) => {
      if (section.startsWith('```')) {
        const codeContent = section.replace(/```(\w+)?\n?/, '').replace(/```$/, '');
        const language = section.match(/```(\w+)/)?.[1] || 'javascript';

        return (
          <div key={index} className="my-4 border border-gray-300 rounded-lg overflow-hidden">
            <div className="flex justify-between items-center bg-gray-100 px-4 py-2 border-b border-gray-300">
              <span className="text-xs text-gray-600 font-medium">{language}</span>
              <button
                onClick={() => copyToClipboard(codeContent)}
                className="flex items-center gap-1 px-2 py-1 bg-gray-200 hover:bg-gray-300 rounded text-xs text-gray-700 transition-colors"
              >
                <Copy size={12} />
                Copy
              </button>
            </div>
            <pre className="bg-gray-900 text-green-400 p-4 overflow-x-auto m-0 font-mono text-sm leading-relaxed">
              <code>{codeContent}</code>
            </pre>
          </div>
        );
      }

      if (section.startsWith('#')) {
        const level = section.match(/^#{1,6}/)[0].length;
        const text = section.replace(/^#{1,6}\s*/, '');

        const headingClasses = {
          1: "text-2xl font-bold mt-6 mb-4 text-gray-900",
          2: "text-xl font-bold mt-5 mb-3 text-gray-900", 
          3: "text-lg font-bold mt-4 mb-2 text-gray-900",
          4: "text-base font-bold mt-3 mb-2 text-gray-900",
          5: "text-sm font-bold mt-2 mb-1 text-gray-900",
          6: "text-xs font-bold mt-2 mb-1 text-gray-900"
        };

        return React.createElement(`h${level}`, {
          key: index,
          className: headingClasses[level]
        }, text);
      }

      if (section.trim()) {
        return (
          <p key={index} className="mb-3 text-gray-800 leading-relaxed">
            {section.trim()}
          </p>
        );
      }

      return null;
    }).filter(Boolean);
  };

  // Typing animation function
  const typeMessage = (message, chatId) => {
    setIsTyping(true);
    const words = message.split(' ');
    let currentText = '';
    let wordIndex = 0;

    const typeInterval = setInterval(() => {
      if (wordIndex < words.length) {
        currentText += (wordIndex > 0 ? ' ' : '') + words[wordIndex];

        setChats((prevChats) =>
          prevChats.map((chat) => {
            if (chat.id === chatId) {
              const messages = [...chat.messages];
              if (messages.length > 0 && messages[messages.length - 1].role === "bot") {
                messages[messages.length - 1] = {
                  ...messages[messages.length - 1],
                  content: currentText,
                  isTyping: true
                };
              }
              return { ...chat, messages };
            }
            return chat;
          })
        );

        wordIndex++;
      } else {
        setIsTyping(false);
        setChats((prevChats) =>
          prevChats.map((chat) => {
            if (chat.id === chatId) {
              const messages = [...chat.messages];
              if (messages.length > 0 && messages[messages.length - 1].role === "bot") {
                messages[messages.length - 1] = {
                  ...messages[messages.length - 1],
                  isTyping: false
                };
              }
              return { ...chat, messages };
            }
            return chat;
          })
        );
        clearInterval(typeInterval);
      }
    }, 50);
  };

  // Handle sending message
  const handleSend = async () => {
    if (!input.trim() || isTyping) return;

    let chatId = currentChatId;
    let updatedChats = [...chats];

    if (!chatId) {
      const newChat = {
        id: Date.now(),
        title: input.length > 30 ? input.substring(0, 30) + "..." : input,
        messages: [],
        createdAt: new Date()
      };
      updatedChats = [newChat, ...updatedChats];
      chatId = newChat.id;
      setCurrentChatId(chatId);
    }

    const userMessage = { role: "user", content: input.trim() };
    updatedChats = updatedChats.map((chat) => {
      if (chat.id === chatId) {
        return { ...chat, messages: [...chat.messages, userMessage] };
      }
      return chat;
    });

    setChats(updatedChats);
    setInput("");

    const botMessage = { role: "bot", content: "", isTyping: true };
    setChats((prevChats) =>
      prevChats.map((chat) =>
        chat.id === chatId ? { ...chat, messages: [...chat.messages, botMessage] } : chat
      )
    );

    try {
      const res = await fetch("http://localhost:3000/api/generate");
      const data = await res.json();
      typeMessage(data.generated, chatId);
    } catch (err) {
      console.error("Error fetching response:", err);
      setIsTyping(false);
      const errorMessage = { 
        role: "bot", 
        content: "Sorry, I encountered an error while generating the response. Please try again.", 
        isTyping: false 
      };

      setChats((prevChats) =>
        prevChats.map((chat) => {
          if (chat.id === chatId) {
            const messages = [...chat.messages];
            messages[messages.length - 1] = errorMessage;
            return { ...chat, messages };
          }
          return chat;
        })
      );
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleNewChat = () => {
    const newChat = { 
      id: Date.now(), 
      title: "New Chat", 
      messages: [],
      createdAt: new Date()
    };
    setChats([newChat, ...chats]);
    setCurrentChatId(newChat.id);
    setInput("");
  };

  const handleSelectChat = (id) => {
    if (isTyping) return;
    setCurrentChatId(id);
    setInput("");
    setEditingChatId(null);
  };

  const startEditingTitle = (id, currentTitle) => {
    setEditingChatId(id);
    setTempTitle(currentTitle);
  };

  const saveEditedTitle = (id) => {
    if (tempTitle.trim()) {
      setChats((prevChats) =>
        prevChats.map((chat) => (chat.id === id ? { ...chat, title: tempTitle.trim() } : chat))
      );
    }
    setEditingChatId(null);
    setTempTitle("");
  };

  const cancelEditing = () => {
    setEditingChatId(null);
    setTempTitle("");
  };

  return (
    <div className="min-h-screen bg-white text-gray-900 flex">
      {/* Sidebar */}
      <div className="w-64 bg-gray-900 text-white flex flex-col">
        <div className="p-4">
          <button 
            onClick={handleNewChat}
            className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-gray-800 hover:bg-gray-700 rounded-lg transition-colors"
          >
            <Plus size={16} />
            New chat
          </button>
        </div>

        <div className="flex-1 overflow-y-auto px-2">
          {chats.map((chat) => (
            <div
              key={chat.id}
              className={`group flex items-center gap-3 p-3 rounded-lg cursor-pointer transition-colors mb-1 ${
                chat.id === currentChatId ? "bg-gray-800" : "hover:bg-gray-800"
              }`}
              onClick={() => handleSelectChat(chat.id)}
            >
              <MessageSquare size={16} className="text-gray-400 flex-shrink-0" />

              {editingChatId === chat.id ? (
                <div className="flex-1 flex items-center gap-2">
                  <input
                    type="text"
                    value={tempTitle}
                    onChange={(e) => setTempTitle(e.target.value)}
                    className="flex-1 bg-gray-700 text-white px-2 py-1 rounded text-sm"
                    autoFocus
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') saveEditedTitle(chat.id);
                      if (e.key === 'Escape') cancelEditing();
                    }}
                  />
                  <button 
                    onClick={() => saveEditedTitle(chat.id)} 
                    className="text-green-400 hover:text-green-300"
                  >
                    <Check size={14} />
                  </button>
                  <button 
                    onClick={cancelEditing} 
                    className="text-red-400 hover:text-red-300"
                  >
                    <X size={14} />
                  </button>
                </div>
              ) : (
                <>
                  <span className="flex-1 truncate text-sm">{chat.title}</span>
                  {chat.id === currentChatId && (
                    <button
                      className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-white transition-opacity"
                      onClick={(e) => {
                        e.stopPropagation();
                        startEditingTitle(chat.id, chat.title);
                      }}
                    >
                      <Edit3 size={14} />
                    </button>
                  )}
                </>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col">
        {currentChat ? (
          <>
            <div className="border-b border-gray-200 p-4">
              <h1 className="text-lg font-medium">{currentChat.title}</h1>
            </div>

            <div className="flex-1 overflow-y-auto p-4 space-y-6">
              {currentChat.messages.map((msg, idx) => (
                <div key={idx} className="max-w-none">
                  {msg.role === "user" ? (
                    <div className="flex justify-end">
                      <div className="max-w-3xl bg-blue-500 text-white p-4 rounded-2xl rounded-tr-md">
                        {msg.content}
                      </div>
                    </div>
                  ) : (
                    <div className="flex justify-start">
                      <div className="max-w-3xl">
                        <div className="bg-gray-100 p-4 rounded-2xl rounded-tl-md">
                          <div className="prose prose-sm max-w-none">
                            {renderBotMessage(msg.content)}
                            {msg.isTyping && (
                              <span className="inline-block w-2 h-5 bg-gray-900 animate-pulse ml-1">|</span>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            <div className="border-t border-gray-200 p-4">
              <div className="max-w-4xl mx-auto">
                <div className="flex items-end gap-3 bg-gray-100 rounded-2xl p-2">
                  <textarea
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={handleKeyPress}
                    placeholder="Message ChatGPT..."
                    disabled={isTyping}
                    className="flex-1 bg-transparent p-2 resize-none border-none outline-none max-h-32 min-h-6"
                    rows={1}
                  />
                  <button
                    onClick={handleSend}
                    disabled={!input.trim() || isTyping}
                    className={`p-2 rounded-lg transition-colors ${
                      !input.trim() || isTyping
                        ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                        : "bg-blue-500 text-white hover:bg-blue-600"
                    }`}
                  >
                    <Send size={16} />
                  </button>
                </div>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center space-y-6">
              <h1 className="text-4xl font-bold text-gray-900">ChatGPT Clone</h1>
              <p className="text-gray-600 text-lg max-w-md">
                Start a conversation by creating a new chat or selecting an existing one.
              </p>
              <button 
                onClick={handleNewChat}
                className="inline-flex items-center gap-2 bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition-colors"
              >
                <Plus size={20} />
                Start New Chat
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
