import React, { useState, useEffect } from "react";
import axios from "axios";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { materialLight } from "react-syntax-highlighter/dist/esm/styles/prism";
import "./App.css";

function App() {
  const [chats, setChats] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [input, setInput] = useState("");

  // Load chats from localStorage
  useEffect(() => {
    const storedChats = JSON.parse(localStorage.getItem("chats")) || [];
    setChats(storedChats);
    if (storedChats.length > 0) setCurrentChatId(storedChats[0].id);
  }, []);

  // Save chats to localStorage whenever chats change
  useEffect(() => {
    localStorage.setItem("chats", JSON.stringify(chats));
  }, [chats]);

  // Auto-create chat if none selected
  const ensureCurrentChat = () => {
    if (!currentChatId) {
      const newChat = { id: Date.now(), title: "New Chat", messages: [] };
      setChats([newChat, ...chats]);
      setCurrentChatId(newChat.id);
      return newChat.id;
    }
    return currentChatId;
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input) return;

    // Ensure there is a current chat
    const chatId = ensureCurrentChat();

    // Update chat messages with user input
    let updatedChats = chats.map((chat) => {
      if (chat.id === chatId) {
        return {
          ...chat,
          messages: [...chat.messages, { role: "user", content: input }],
        };
      }
      return chat;
    });

    // If chat is newly created (title is "New Chat") update title to first user message
    updatedChats = updatedChats.map((chat) => {
      if (chat.id === chatId && chat.title === "New Chat") {
        return { ...chat, title: input.substring(0, 30) + "..." };
      }
      return chat;
    });

    setChats(updatedChats);
    setInput("");

    // Fetch response from server (static for now)
    try {
      const res = await axios.get("http://localhost:3000/api/generate"); // staticResponse
      const botMessage = { role: "bot", content: res.data.generated };

      setChats((prevChats) =>
        prevChats.map((chat) =>
          chat.id === chatId
            ? { ...chat, messages: [...chat.messages, botMessage] }
            : chat
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  const handleNewChat = () => {
    const newChat = { id: Date.now(), title: "New Chat", messages: [] };
    setChats([newChat, ...chats]);
    setCurrentChatId(newChat.id);
    setInput("");
  };

  const currentChat = chats.find((chat) => chat.id === currentChatId);

  return (
    <div className="App">
      <div className="sidebar">
        <button className="new-chat" onClick={handleNewChat}>
          + New Chat
        </button>
        <ul className="chat-list">
          {chats.map((chat) => (
            <li
              key={chat.id}
              className={chat.id === currentChatId ? "active" : ""}
              onClick={() => setCurrentChatId(chat.id)}
            >
              {chat.title || "Untitled Chat"}
            </li>
          ))}
        </ul>
      </div>

      <div className="chat-window">
        <div className="messages">
          {currentChat?.messages.map((msg, idx) => (
            <div
              key={idx}
              className={`message ${msg.role === "bot" ? "bot" : "user"}`}
            >
              {msg.role === "bot" ? (
                <ReactMarkdown
                  children={msg.content}
                  remarkPlugins={[remarkGfm]}
                  components={{
                    code({ node, inline, className, children, ...props }) {
                      return !inline ? (
                        <SyntaxHighlighter
                          style={materialLight}
                          language="javascript"
                          {...props}
                        >
                          {String(children).replace(/\n$/, "")}
                        </SyntaxHighlighter>
                      ) : (
                        <code className={className} {...props}>
                          {children}
                        </code>
                      );
                    },
                  }}
                />
              ) : (
                <p>{msg.content}</p>
              )}
            </div>
          ))}
        </div>

        <form className="input-area" onSubmit={handleSend}>
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
}

export default App;
